<?phpif ( ! defined( 'ABSPATH' ) ) {	exit;}/** * Scripts Class * Handles Scripts and Styles enqueues functionality. * * @class  UWA_Twilio_SMS_Scripts * @package Ultimate WooCommerce Auction Pro - Twilio SMS Addon * @author Nitesh Singh  * @since 1.0 * */class UWA_Twilio_SMS_Scripts {		private static $instance;	/**	 * Returns the *Singleton* instance of this class.	 *	 * @return Singleton The *Singleton* instance.	 *	 */    public static function get_instance() {        if ( null === self::$instance ) {            self::$instance = new self();        }        return self::$instance;    }    /**	 * Plugin actions	 *	 */	public function __construct() {				/* Add admin side scripts */		add_action( 'admin_enqueue_scripts', array( $this, 			'uwa_twilio_sms_register_admin_scripts') );	}	/**	 * Manage admin side scripts	 	 *		 */	public function uwa_twilio_sms_register_admin_scripts( $hook_sufix ) {		$uwa_twilio_data = array(			'test_error_message' => __('Please make sure you have entered a mobile phone number and test message.', 'woo_ua'),					);		/* Register globally scripts */		wp_register_script( 'uwa-twilio-sms-admin', UW_AUCTION_PRO_ASSETS_URL . '/js/addons/uwa-twilio-sms-admin.js', array('jquery'), UW_AUCTION_PRO_VERSION );						wp_localize_script('uwa-twilio-sms-admin', 'UWA_TWILIO_SMS', array('ajaxurl' => admin_url('admin-ajax.php'), 'UWA_TWILIO_SMS_NONCE' => 			wp_create_nonce('UtAajax-nonce') ));		wp_localize_script('uwa-twilio-sms-admin', 'uwa_twilio_data', $uwa_twilio_data);		wp_enqueue_script( 'uwa-twilio-sms-admin' );	}	}UWA_Twilio_SMS_Scripts::get_instance();