<?phpif (!defined('ABSPATH')) {	exit;}if ( ! class_exists( 'UWA_Email_Auction_Watchlist' ) ) {    /**     *     * @class UWA_Email_Auction_Watchlist     * @package Ultimate WooCommerce Auction PRO     * @author Nitesh Singh      * @since 1.0      *     */    class UWA_Email_Auction_Watchlist extends WC_Email {        /**         * Define email related information         *                  */        public function __construct() {			            $this->id = 'woo_ua_email_auction_watchlist';            $this->title = __('Ultimate Auction - Watchlist', 'woo_ua');            $this->customer_email = true;                       $this->description = __('Email is sent to watchlist users when a bid is placed', 'woo_ua');            $this->heading = __('{site_title} - Bid was placed on an auction product which was in your watchlist.',                 'woo_ua');            $this->subject = __('{site_title} - Bid was placed on an auction product which was in your watchlist.',                 'woo_ua');            $this->template_html = 'emails/watchlist.php';            $this->template_plain = 'emails/plain/watchlist.php';            /* Trigger on bid overbidded by other bidder */            add_action( 'uwa_pro_watchlist_email', array( $this, 'trigger' ), 10, 2 );            /* Call parent constructor to load any other defaults not explicity defined here */            parent::__construct();        }        /**         * Define trigger         *         * @param object $product, $outbiddeduser         *         */        public function trigger( $product_data, $watchlistusers ) {                      if ( !$this->is_enabled() ) {                return;            }            if (count( $watchlistusers ) > 0) {                                foreach ($watchlistusers as $key => $value) {                    $user = get_user_by('id', $value);                    $url_product = get_permalink($product_data->get_id());                    $this->object = array(                        'user_email'    => $user->data->user_email,                        'user_name'     => $user->data->user_login,                        'product_name'  => $product_data->get_title(),                        'product'       => $product_data,                        'url_product'   => $url_product,                    );                                    $this->send($user->data->user_email, $this->get_subject(), $this->get_content(), $this->get_headers(),                        $this->get_attachments());                }            } /* end of if */                    }        /**         * Get content html          *         * @return array         *         */        public function get_content_html() {            return wc_get_template_html( $this->template_html,                 array(                    'email_heading' => $this->get_heading(),                    'sent_to_admin' => true,                    'plain_text'    => false,                    'email'         => $this                ), '', UW_AUCTION_PRO_WC_TEMPLATE );        }        /**         * Get content plain          *         * @return array         *         */        public function get_content_plain() {            return wc_get_template_html( $this->template_plain,                 array(                    'email_heading' => $this->get_heading(),                    'sent_to_admin' => true,                    'plain_text'    => true,                    'email'         => $this                ), '', UW_AUCTION_PRO_WC_TEMPLATE );        }        /**         * Add form fields for email                 *         */        public function init_form_fields() {            $this->form_fields = array(                'enabled'    => array(                    'title'   => __( 'Enable/Disable', 'woocommerce' ),                    'type'    => 'checkbox',                    'label'   => __( 'Enable this email notification', 'woocommerce' ),                    'default' => 'yes'                ),                               'subject'    => array(                    'title'       => __( 'Subject', 'woocommerce' ),                    'type'        => 'text',                    'description' => sprintf( __( 'This controls the email subject line. Leave blank to use the default subject: <code>%s</code>.', 'woo_ua' ), $this->subject ),                    'placeholder' => '',                    'default'     => '',                    'desc_tip'    => true                ),                'heading'    => array(                    'title'       => __( 'Email Heading', 'woo_ua' ),                    'type'        => 'text',                    'description' => sprintf( __( 'This controls the main heading contained within the email notification. Leave blank to use the default heading: <code>%s</code>.', 'woo_ua' ), $this->heading ),                    'placeholder' => '',                    'default'     => '',                    'desc_tip'    => true                ),                                'email_type' => array(                    'title'       => __( 'Email type', 'woocommerce' ),                    'type'        => 'select',                    'description' => __( 'Choose which format of email to send.', 'woocommerce' ),                    'default'     => 'html',                    'class'       => 'email_type wc-enhanced-select',                    'options'     => $this->get_email_type_options(),                    'desc_tip'    => true                )            );        }    }}return new UWA_Email_Auction_Watchlist();