<?php/** * Bidder placed a bid email notification (plain) *  * @package Ultimate WooCommerce Auction PRO * @author Nitesh Singh  * @since 1.0   * */if (!defined('ABSPATH')) {    exit;}global $woocommerce;  $product_id = $email->object['product_id'];   $product = wc_get_product($product_id);    $product_base_currency = $product->uwa_aelia_get_base_currency();   $args = array("currency" => $product_base_currency);    $user_name = $email->object['user_name'];  $auction_title = $product->get_title();  $auction_bid_value = wc_price($product->get_uwa_current_bid(), $args);  $thumb_image = $product->get_image( 'thumbnail' );  $checkout_url  = add_query_arg( array( 'pay-uwa-auction' => $product_id  ), uwa_auction_get_checkout_url() );  $user_type = $email->object['user_type'];  $winner_id = $email->object['user_id'];  $auction_owner_name = $email->object['owner_name'];   $auction_owner_id = $email->object['owner_id'];    /* change payment url for auto order */        $uwa_auto_order_enable = get_option('uwa_auto_order_enable');        if($uwa_auto_order_enable == "yes"){                          $productid = $product->get_id();            $uwa_order_id = get_post_meta( $productid, 'woo_ua_order_id', true);            if ($uwa_order_id > 0){              $order = wc_get_order($uwa_order_id);            $checkout_url = $order->get_checkout_payment_url();          }               }   echo $email_heading . "</br><Br>";  if($user_type === "bidder"){  /* buyer */    $auction_url = $email->object['url_product'];     $userlink = add_query_arg( 'user_id', $email->object['user_id'] ,       admin_url( 'user-edit.php' ) );       printf( __( "Hi %s,", 'woo_ua' ), $user_name);        echo "</br>";    printf( __( 'Congratulations! You are the winner! of the auction product <a href="%s">%s</a>.', 'woo_ua' ), $auction_url, $auction_title);    echo "</br>";    printf( __( "Winning bid %s.", 'woo_ua' ), $auction_bid_value);    echo "</br>";       /* -----------   when offline_dealing addon is active   ------------- */      $addons = uwa_enabled_addons();      if(is_array($addons) && in_array('uwa_offline_dealing_addon', $addons)){          /* 1. pay now button */          // buyers and stripe both deactive          if(!in_array('uwa_buyers_premium_addon', $addons) &&             !in_array('uwa_stripe_auto_debit_addon', $addons)){            //echo "in 1"; no need to display button           } // buyers active only          elseif(in_array('uwa_buyers_premium_addon', $addons) &&             !in_array('uwa_stripe_auto_debit_addon', $addons)){            //echo "in 2";             $button_text = __("Pay Buyer's Premium", 'woo_ua');            do_action( 'woocommerce_email_before_pay_button', $product);             printf( __( 'Please, proceed to checkout, <a href="%s">%s</a>.',              'woo_ua' ), $checkout_url, $button_text);            echo "</br>";          }  // buyers and stripe both active          elseif(in_array('uwa_buyers_premium_addon', $addons) &&             in_array('uwa_stripe_auto_debit_addon', $addons)){              //echo "in 3";              /*echo __( "Buyer's premium for auction product is auto debited via                 stripe", 'woo_ua' );              echo "</br>";*/          }            /* 2.  display SELLER CONTACT details */              /*  ------------------------------------------------  */              $addons = uwa_enabled_addons();              if(is_array($addons) && in_array('uwa_offline_dealing_addon', $addons)){                  $display_contact_detail = get_option(                    "offline_dealing_display_in_mail", "no");                  if($display_contact_detail == "yes"){                      echo "<br>";                      _e('Seller contact details :', 'woo_ua');                      $user_meta = get_userdata($auction_owner_id);                      $user_roles = $user_meta->roles;                      // when seller has store vendor role                       if (in_array( 'wcfm_vendor', $user_roles, true )) {                                        // display vendor's store address - seller auction                             $vendor_profile = get_user_meta($auction_owner_id,                               "wcfmmp_profile_settings");                            if(is_array($vendor_profile)){                                  $vdata = $vendor_profile[0];                                  $store_name = $vdata['store_name'];                                  $store_email = $vdata['store_email'];                                  $add_1 = $vdata['address']['street_1'];                                  $add_2 = $vdata['address']['street_2'];                                  $city = $vdata['address']['city'];                                  $country = $vdata['address']['country'];                                  $postcode = $vdata['address']['zip'];                                  $phone = $vdata['phone'];                                  $display_contact = "<br><p><strong>" .                                    __('Store Address:', 'woo_ua');                                  $display_contact .= "</strong><br>". $store_name . "<br>";                                  if($add_1)                                    $display_contact .= $add_1 . "<br>";                                  if($add_2)                                    $display_contact .= $add_2 . "<br>";                                  if($city)                                    $display_contact .= $city . "<br>";                                  if($postcode)                                    $display_contact .= $postcode . "<br>";                                  if($country)                                    $display_contact .= $country . "<br>";                                              echo $display_contact;                                  if($phone){                                    _e('Contact : ', 'woo_ua');                                    echo $phone;                                  }                                    echo "<br>";                                  if($store_email){                                    _e('Email : ', 'woo_ua');                                    echo $store_email;                                  }                                                    } /* end of if - isarray */                      }                      else{                            /* for general */                            $customer_id = $auction_owner_id;                            //get_billing_details_html($customer_id);                            $display_contact = "";                            $country = "";                            $first_name = get_user_meta( $customer_id, 'billing_first_name', true );                            $last_name = get_user_meta( $customer_id, 'billing_last_name', true );                            $email = get_user_meta( $customer_id, 'billing_email', true );                            $add_1 = get_user_meta( $customer_id, 'billing_address_1', true );                            $add_2 = get_user_meta( $customer_id, 'billing_address_2', true );                            $city = get_user_meta( $customer_id, 'billing_city', true );                            $postcode = get_user_meta( $customer_id, 'billing_postcode', true );                            $phone = get_user_meta( $customer_id, 'billing_phone', true );                            $cntry_code = get_user_meta( $customer_id, 'billing_country', true );                            if($cntry_code){                              $country = WC()->countries->countries[$cntry_code].                              "  ($cntry_code)";                            }                                                        echo "<p>";                                                        if($first_name || $last_name){                                  $display_contact = $first_name ." " .$last_name . "<br>";                            }                             if($add_1)                              $display_contact .= $add_1 . "<br>";                            if($add_2)                              $display_contact .= $add_2 . "<br>";                            if($city)                              $display_contact .= $city . "<br>";                            if($postcode)                              $display_contact .= $postcode . "<br>";                            if($country)                              $display_contact .= $country . "<br><br>";                                echo $display_contact;                            if($phone){                              _e('Contact : ', 'woo_ua');                              echo $phone;                            }                            echo "<br>";                            if($email){                              _e('Email : ', 'woo_ua');                              echo $email;                            }                                                        echo "</p>";                      } /* end of else */                  } /* end of if  - contact detail */              } /* end of if  - addons */            /*  ------------------------------------------------  */      }      else{                  /* set button text */          $button_text = __('Pay Now', 'woo_ua');          $w_current_userid = $product->get_uwa_auction_current_bider();          $w_product_price = $product->get_uwa_auction_current_bid();          $w_product_id = $product->get_id();          $get_charged_for_winner = get_option(              "_uwa_w_s_charge_".$w_product_id."_".$w_current_userid, false);          if($get_charged_for_winner == $w_product_price){             $button_text = __('Get Item', 'woo_ua');          }          do_action( 'woocommerce_email_before_pay_button', $product);           printf( __( 'Please, proceed to checkout, <a href="%s">%s</a>.',             'woo_ua' ), $checkout_url, $button_text);          echo "</br>";      } /* end of else - addon */   }  echo apply_filters( 'woocommerce_email_footer_text', get_option( 'woocommerce_email_footer_text' ) );